import React, { useState, useEffect } from "react";
import { useSwipeable } from "react-swipeable";

const Wrapped = () => {
  const images = Array.from({ length: 14 }, (_, i) => `/images/${i + 1}.png`);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [progress, setProgress] = useState(0);

  // Time in milliseconds for each slide
  const slideDuration = 5000;

  // Timer for automatic slide change
  useEffect(() => {
    if (isPaused) return;

    const interval = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
          return 0;
        }
        return prev + (100 / (slideDuration / 100));
      });
    }, 100);

    return () => clearInterval(interval);
  }, [isPaused, currentIndex]);

  // Handlers for swiping and pausing
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => handleTap(),
    onSwipedRight: () => setCurrentIndex((prev) => (prev - 1 + images.length) % images.length),
  });

  const handleTap = () => {
    setProgress(0);
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  const handleHold = () => {
    setIsPaused(true);
  };

  const handleRelease = () => {
    setIsPaused(false);
  };

  return (
    <div
      className="h-screen w-screen bg-black flex items-center justify-center"
      {...swipeHandlers}
    >
      <div
        className="relative w-[375px] h-[667px] bg-black overflow-hidden flex items-center justify-center"
        onTouchStart={handleHold}
        onTouchEnd={handleRelease}
        onClick={handleTap}
      >
        {/* Progress Bar */}
        <div className="absolute top-0 left-0 w-full h-1 bg-gray-700">
          <div
            className="h-full bg-green-500"
            style={{ width: `${progress}%`, transition: isPaused ? "none" : "width 0.1s linear" }}
          ></div>
        </div>

        {/* Image Slide */}
        <img
          src={images[currentIndex]}
          alt={`Slide ${currentIndex + 1}`}
          className="absolute inset-0 w-full h-full object-cover"
        />
      </div>
    </div>
  );
};

export default Wrapped;
