import React, { useState, useEffect, useRef } from 'react';
import { useSwipeable } from 'react-swipeable';

const WrappedSlideshow = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [progress, setProgress] = useState(0);
  const [isLongPress, setIsLongPress] = useState(false);
  const longPressTimer = useRef(null);
  const progressTimer = useRef(null);

  const images = Array.from({ length: 14 }, (_, i) => `/images/${i + 1}.png`);
  const slideDuration = 5000; // 5 seconds per slide

  const resetProgress = () => {
    setProgress(0);
    if (progressTimer.current) {
      clearInterval(progressTimer.current);
    }
    startProgressTimer();
  };

  const startProgressTimer = () => {
    progressTimer.current = setInterval(() => {
      if (!isPaused) {
        setProgress(prev => {
          if (prev >= 100) {
            handleNext();
            return 0;
          }
          return prev + (100 / (slideDuration / 100));
        });
      }
    }, 100);
  };

  const handleNext = () => {
    if (currentIndex < images.length - 1) {
      setCurrentIndex(prev => prev + 1);
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(prev => prev - 1);
    }
  };

  const handleTouchStart = (e) => {
    // Get touch position
    const touch = e.touches[0];
    const screenWidth = window.innerWidth;
    const touchX = touch.clientX;

    // Determine if touch is on left or right side of screen
    if (touchX < screenWidth / 2) {
      // Left side - start long press timer
      longPressTimer.current = setTimeout(() => {
        setIsLongPress(true);
        setIsPaused(true);
      }, 200);
    } else {
      // Right side - handle next slide
      handleNext();
      resetProgress();
    }
  };

  const handleTouchEnd = () => {
    if (longPressTimer.current) {
      clearTimeout(longPressTimer.current);
    }
    setIsLongPress(false);
    setIsPaused(false);
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => {
      handleNext();
      resetProgress();
    },
    onSwipedRight: () => {
      handlePrevious();
      resetProgress();
    },
    preventDefaultTouchmoveEvent: true,
    trackMouse: true
  });

  useEffect(() => {
    startProgressTimer();
    return () => {
      if (progressTimer.current) {
        clearInterval(progressTimer.current);
      }
      if (longPressTimer.current) {
        clearTimeout(longPressTimer.current);
      }
    };
  }, [currentIndex, isPaused]);

  return (
    <div className="min-h-screen w-full bg-black flex items-center justify-center">
      <div className="relative w-[375px] h-[667px] bg-black overflow-hidden" {...swipeHandlers}>
        {/* Progress Bar */}
        <div className="absolute top-0 left-0 w-full h-1.5 bg-gray-800 z-20">
          <div
            className="h-full bg-white transition-all duration-100 ease-linear"
            style={{ 
              width: `${progress}%`,
              transition: isPaused ? 'none' : 'width 0.1s linear'
            }}
          />
        </div>

        {/* Slide Counter */}
        <div className="absolute top-4 right-4 z-20 bg-black/50 px-2 py-1 rounded-full">
          <span className="text-white text-sm">
            {currentIndex + 1}/{images.length}
          </span>
        </div>

        {/* Touch Areas */}
        <div 
          className="absolute inset-0 z-10 flex"
          onTouchStart={handleTouchStart}
          onTouchEnd={handleTouchEnd}
        >
          {/* Left side - long press area */}
          <div className="w-1/2 h-full" />
          {/* Right side - next slide area */}
          <div className="w-1/2 h-full" />
        </div>

        {/* Hold Indicator */}
        {isLongPress && (
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-20">
            <div className="bg-white/20 backdrop-blur-sm px-4 py-2 rounded-lg">
              <span className="text-white text-sm">Hold to pause</span>
            </div>
          </div>
        )}

        {/* Images */}
        <div className="relative w-full h-full">
          <img
            src={images[currentIndex]}
            alt={`Slide ${currentIndex + 1}`}
            className="absolute inset-0 w-full h-full object-contain transition-opacity duration-300"
          />
        </div>
      </div>
    </div>
  );
};

export default WrappedSlideshow;
