import React, { useState } from 'react';

const ImageSlide = ({ image, onNext, onPause, onResume }) => {
  const [isPaused, setIsPaused] = useState(false);

  const handleTap = () => {
    if (!isPaused) onNext();
  };

  const handleLongPress = () => {
    setIsPaused(true);
    onPause();
  };

  const handleRelease = () => {
    setIsPaused(false);
    onResume();
  };

  return (
    <div
      style={{
        position: 'relative',
        width: '100%',
        maxWidth: '375px', // Typical mobile width
        aspectRatio: '9 / 16', // Portrait ratio
        margin: '0 auto', // Center the container
        background: `url(${image}) center/cover no-repeat`,
      }}
      onClick={handleTap}
      onMouseDown={handleLongPress}
      onMouseUp={handleRelease}
      onTouchStart={handleLongPress}
      onTouchEnd={handleRelease}
    >
      <p
        style={{
          position: 'absolute',
          bottom: '20px',
          color: '#fff',
          textAlign: 'center',
          width: '100%',
        }}
      >
        Tap to skip, Long press to pause
      </p>
    </div>
  );
};

export default ImageSlide;
