import React, { useEffect, useState } from 'react';

const ProgressBar = ({ duration, isPaused, onComplete, onSkip, color = '#00f' }) => {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    if (isPaused) return;
    const interval = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          clearInterval(interval);
          onComplete();
          return 100;
        }
        return prev + 100 / (duration / 100);
      });
    }, 100);
    return () => clearInterval(interval);
  }, [duration, isPaused, onComplete]);

  const handleClick = (e) => {
    const clickedPosition = e.clientX / window.innerWidth;
    const newProgress = Math.floor(clickedPosition * 100);
    onSkip(newProgress);
  };

  return (
    <div
      style={{ height: '5px', background: '#ccc', width: '100%' }}
      onClick={handleClick}
    >
      <div
        style={{
          height: '100%',
          width: `${progress}%`,
          background: color,
          transition: 'width 0.1s',
        }}
      ></div>
    </div>
  );
};

export default ProgressBar;
