import React, { useState, useEffect, useCallback } from "react";
import { useSwipeable } from "react-swipeable";

const Wrapped = () => {
  const images = Array.from({ length: 12 }, (_, i) => `/images/${i + 1}.png`);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [progress, setProgress] = useState(0);
  const [touchStartTime, setTouchStartTime] = useState(0);
  const slideDuration = 5000;

  const resetProgress = useCallback(() => {
    setProgress(0);
  }, []);

  const handleNext = useCallback(() => {
    resetProgress();
    setCurrentIndex((prev) => (prev + 1) % images.length);
  }, [resetProgress, images.length]);

  const handlePrevious = useCallback(() => {
    resetProgress();
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  }, [resetProgress, images.length]);

  useEffect(() => {
    if (isPaused) return;

    const interval = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          handleNext();
          return 0;
        }
        return prev + (100 / (slideDuration / 100));
      });
    }, 100);

    return () => clearInterval(interval);
  }, [isPaused, handleNext, slideDuration]);

  const swipeHandlers = useSwipeable({
    onSwipedLeft: handleNext,
    onSwipedRight: handlePrevious,
    preventDefaultTouchmoveEvent: true,
    trackMouse: true
  });

  const handleTouchStart = (e) => {
    setTouchStartTime(Date.now());
    setIsPaused(true);
  };

  const handleTouchEnd = (e) => {
    const touchDuration = Date.now() - touchStartTime;
    setIsPaused(false);

    if (touchDuration < 200) {
      const touchX = e.changedTouches ? e.changedTouches[0].clientX : e.clientX;
      const element = e.currentTarget;
      const rect = element.getBoundingClientRect();
      const isRightSide = touchX > rect.left + rect.width / 2;

      if (isRightSide) {
        handleNext();
      } else {
        handlePrevious();
      }
    }
  };

  return (
    <div className="min-h-screen w-full bg-black flex items-center justify-center">
      <div 
        className="relative w-full max-w-[375px] h-[667px] bg-black overflow-hidden"
        style={{ aspectRatio: '375/667' }}
        {...swipeHandlers}
        onTouchStart={handleTouchStart}
        onTouchEnd={handleTouchEnd}
        onMouseDown={handleTouchStart}
        onMouseUp={handleTouchEnd}
      >
        {/* Segmented Progress Bar */}
        <div className="absolute top-4 left-4 right-4 flex gap-1 z-20">
          {images.map((_, index) => (
            <div 
              key={index}
              className="h-1 flex-1 rounded-full overflow-hidden bg-white/30"
            >
              <div
                className={`h-full bg-white transition-all duration-200 rounded-full ${
                  index < currentIndex ? 'w-full' :
                  index === currentIndex ? '' : 'w-0'
                }`}
                style={{
                  width: index === currentIndex ? `${progress}%` : undefined,
                  transition: isPaused && index === currentIndex ? 'none' : 'width 0.1s linear'
                }}
              />
            </div>
          ))}
        </div>

        {/* Current Image */}
        <div className="relative w-full h-full">
          <img
            src={images[currentIndex]}
            alt={`Slide ${currentIndex + 1}`}
            className="absolute inset-0 w-full h-full object-cover transition-opacity duration-300"
          />
        </div>

        {/* Touch Areas */}
        <div className="absolute inset-0 z-10 flex">
          <div className="w-1/2 h-full" />
          <div className="w-1/2 h-full" />
        </div>

        {/* Slide Indicators */}
        <div className="absolute bottom-8 left-0 right-0 flex justify-center items-center gap-2 z-20">
          {images.map((_, index) => (
            <div
              key={index}
              className={`w-2 h-2 rounded-full transition-all duration-300 ${
                index === currentIndex 
                  ? 'bg-white scale-125' 
                  : 'bg-white/50 scale-100'
              }`}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Wrapped;
