import React, { useState, useEffect } from 'react';
import ImageSlide from './ImageSlide';
import ProgressBar from './ProgressBar';

const Wrapper = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  // Preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, [images]);

  const handleNext = () => {
    if (currentIndex < images.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handleSkip = (progress) => {
    const newIndex = Math.floor(progress / (100 / images.length));
    setCurrentIndex(newIndex < images.length ? newIndex : images.length - 1);
  };

  const colors = ['#f00', '#0f0', '#00f', '#ff0', '#0ff'];
  const durations = [5000, 7000, 3000, 4000, 6000, 5000, 7000, 3000, 4000, 6000, 5000, 7000];
  const currentColor = colors[currentIndex % colors.length];
  const currentDuration = durations[currentIndex];

  return (
    <div style={{ position: 'relative', height: '100vh' }}>
      <ImageSlide
        image={images[currentIndex]}
        onNext={handleNext}
        onPause={() => setIsPaused(true)}
        onResume={() => setIsPaused(false)}
      />
      <ProgressBar
        duration={currentDuration}
        isPaused={isPaused}
        onComplete={handleNext}
        color={currentColor}
        onSkip={handleSkip}
      />
    </div>
  );
};

export default Wrapper;
